import { useState } from 'react';

interface LazyImageProps {
  src: string;
  alt: string;
  className?: string;
  placeholder?: string;
  onLoad?: () => void;
  onError?: () => void;
  loading?: 'lazy' | 'eager';
}

const LazyImage = ({ 
  src, 
  alt, 
  className = '', 
  placeholder = 'data:image/svg+xml;base64,',
  onLoad,
  onError,
  loading = 'eager'
}: LazyImageProps) => {
  const [imageSrc, setImageSrc] = useState(src || placeholder);
  const [isLoaded, setIsLoaded] = useState(false);

  const handleLoad = () => {
    setIsLoaded(true);
    onLoad?.();
  };

  const handleError = () => {
    console.error(`Failed to load image: ${src}`);
    onError?.();
  };

  return (
    <img
      src={imageSrc}
      alt={alt}
      className={`${className} ${!isLoaded ? 'animate-pulse bg-gray-200' : ''}`}
      loading={loading}
      onLoad={handleLoad}
      onError={handleError}
    />
  );
};

export default LazyImage;
